sub check_conflicts {
    if ( eval { require './lib/Log/Dispatch/Conflicts.pm'; 1; } ) {
        if ( eval { Log::Dispatch::Conflicts->check_conflicts; 1 } ) {
            return;
        }
        else {
            my $err = $@;
            $err =~ s/^/    /mg;
            warn "***\n$err***\n";
